mapper.update:
UPDATE t_user SET
  name = #{name},
  password = #{password},
  phone = #{phone},
  user_status = #{userStatus}
WHERE id = #{id}

UPDATE t_user SET
  user_status = #{userStatus}
WHERE id = #{id}

mapper.select:
SELECT id, name, password, phone, user_status FROM t_user WHERE user_status != -1
AND name LIKE CONCAT('%',#{name},'%')
AND user_status = #{userStatus}
LIMIT #{pageIndex} , #{pageSize}

SELECT count(1) FROM t_user WHERE user_status != -1
AND name LIKE CONCAT('%',#{name},'%')
AND user_status = #{userStatus}
